des some utility methods for sending mail to an SMTP server."," *"," * @author Chris Ryan"," * @author Marcus Bointon \u003cphpmailer@synchromedia.co.uk\u003e"," */","class SMTP","{","    /**","     * The PHPMailer SMTP version number.","     *","     * @var string","     */","    const VERSION = '6.10.0';","","    /**","     * SMTP line break constant.","     *","     * @var string","     */","    const LE = \"\\r\\n\";","","    /**","     * The SMTP port to use if one is not specified.","     *","     * @var int","     */","    const DEFAULT_PORT = 25;","","    /**","     * The SMTPs port to use if one is not specified.","     *","     * @var int","     */","    const DEFAULT_SECURE_PORT = 465;","","    /**","     * The maximum line length allowed by RFC 5321 section 4.5.3.1.6,","     * *excluding* a trailing CRLF break.","     *","     * @see https://www.rfc-editor.org/rfc/rfc5321#section-4.5.3.1.6","     *","     * @var int","     */","    const MAX_LINE_LENGTH = 998;","","    /**","     * The maximum line length allowed for replies in RFC 5321 section 4.5.3.1.5,","     * *including* a trailing CRLF line break.","     *","     * @see https://www.rfc-editor.org/rfc/rfc5321#section-4.5.3.1.5","     *","     * @var int","     */","    const MAX_REPLY_LENGTH = 512;","","    /**","     * Debug level for no output.","     *","     * @var int","     */","    const DEBUG_OFF = 0;","","    /**","     * Debug level to show client -\u003e server messages.","     *","     * @var int","     */","    const DEBUG_CLIENT = 1;","","    /**","     * Debug level to show client -\u003e server and server -\u003e client messages.","     *","     * @var int","     */","    const DEBUG_SERVER = 2;","","    /**","     * Debug level to show connection status, client -\u003e server and server -\u003e client messages.","     *","     * @var int","     */","    const DEBUG_CONNECTION = 3;","","    /**","     * Debug level to show all messages.","     *","     * @var int","     */","    const DEBUG_LOWLEVEL = 4;","","    /**","     * Debug output level.","     * Options:","     * * self::DEBUG_OFF (`0`) No debug output, default","     * * self::DEBUG_CLIENT (`1`) Client commands","     * * self::DEBUG_SERVER (`2`) Client commands and server responses","     * * self::DEBUG_CONNECTION (`3`) As DEBUG_SERVER plus connection status","     * * self::DEBUG_LOWLEVEL (`4`) Low-level data output, all messages.","     *","     * @var int","     */","    public $do_debug = self::DEBUG_OFF;","","    /**","     * How to handle debug output.","     * Options:","     * * `echo` Output plain-text as-is, appropriate for CLI","     * * `html` Output escaped, line breaks converted to `\u003cbr\u003e`, appropriate for browser output","     * * `error_log` Output to error log as configured in php.ini","     * Alternatively, you can provide a callable expecting two params: a message string and the debug level:","     *","     * ```php","     * $smtp-\u003eDebugoutput = function($str, $level) {echo \"debug level $level; message: $str\";};","     * ```","     *","     * Alternatively, you can pass in an instance of a PSR-3 compatible logger, though only `debug`","     * level output is used:","     *","     * ```php","     * $mail-\u003eDebugoutput = new myPsr3Logger;","     * ```","     *","     * @var string|callable|\\Psr\\Log\\LoggerInterface","     */","    public $Debugoutput = 'echo';","","    /**","     * Whether to use VERP.","     *","     * @see https://en.wikipedia.org/wiki/Variable_envelope_return_path","     * @see https://www.postfix.org/VERP_README.html Info on VERP","     *","     * @var bool","     */","    public $do_verp = false;","","    /**","     * Whether to use SMTPUTF8.","     *","     * @see https://www.rfc-editor.org/rfc/rfc6531","     *","     * @var bool","     */","    public $do_smtputf8 = false;","","    /**","     * The timeout value for connection, in seconds.","     * Default of 5 minutes (300sec) is from RFC2821 section 4.5.3.2.","     * This needs to be quite high to function correctly with hosts using greetdelay as an anti-spam measure.","     *","     * @see https://www.rfc-editor.org/rfc/rfc2821#section-4.5.3.2","     *","     * @var int","     */","    public $Timeout = 300;","","    /**","     * How long to wait for commands to complete, in seconds.","     * Default of 5 minutes (300sec) is from RFC2821 section 4.5.3.2.","     *","     * @var int","     */","    public $Timelimit = 300;","","    /**","     * Patterns to extract an SMTP transaction id from reply to a DATA command.","     * The first capture group in each regex will be used as the ID.","     * MS ESMTP returns the message ID, which may not be correct for internal tracking.","     *","     * @var string[]","     */","    protected $smtp_transaction_id_patterns = [","        'exim' =\u003e '/[\\d]{3} OK id=(.*)/',","        'sendmail' =\u003e '/[\\d]{3} 2\\.0\\.0 (.*) Message/',","        'postfix' =\u003e '/[\\d]{3} 2\\.0\\.0 Ok: queued as (.*)/',","        'Microsoft_ESMTP' =\u003e '/[0-9]{3} 2\\.[\\d]\\.0 (.*)@(?:.*) Queued mail for delivery/',","        'Amazon_SES' =\u003e '/[\\d]{3} Ok (.*)/',","        'SendGrid' =\u003e '/[\\d]{3} Ok: queued as (.*)/',","        'CampaignMonitor' =\u003e '/[\\d]{3} 2\\.0\\.0 OK:([a-zA-Z\\d]{48})/',","        'Haraka' =\u003e '/[\\d]{3} Message Queued \\((.*)\\)/',","        'ZoneMTA' =\u003e '/[\\d]{3} Message queued as (.*)/',","        'Mailjet' =\u003e '/[\\d]{3} OK queued as (.*)/',","    ];","","    /**","     * Allowed SMTP XCLIENT attributes.","     * Must be allowed by the SMTP server. EHLO response is not checked.","     *","     * @see https://www.postfix.org/XCLIENT_README.html","     *","     * @var array","     */","    public static $xclient_allowed_attributes = [","        'NAME', 'ADDR', 'PORT', 'PROTO', 'HELO', 'LOGIN', 'DESTADDR', 'DESTPORT'","    ];","","    /**","     * The last transaction ID issued in response to a DATA command,","     * if one was detected.","     *","     * @var string|bool|null","     */","    protected $last_smtp_transaction_id;","","    /**","     * The socket for the server connection.","     *","     * @var ?resource","     */","    protected $smtp_conn;","","    /**","     * Error information, if any, for the last SMTP command.","     *","     * @var array","     */","    protected $error = [","        'error' =\u003e '',","        'detail' =\u003e '',","        'smtp_code' =\u003e '',","        'smtp_code_ex' =\u003e '',","    ];","","    /**","     * The reply the server sent to us for HELO.","     * If null, no HELO string has yet been received.","     *","     * @var string|null","     */","    protected $helo_rply;","","    /**","     * The set of SMTP extensions sent in reply to EHLO command.","     * Indexes of the array are extension names.","     * Value at index 'HELO' or 'EHLO' (according to command that was sent)","     * represents the server name. In case of HELO it is the only element of the array.","     * Other values can be boolean TRUE or an array containing extension options.","     * If null, no HELO/EHLO string has yet been received.","     *","     * @var array|null","     */","    protected $server_caps;","","    /**","     * The most recent reply received from the server.","     *","     * @var string","     */","    protected $last_reply = '';","","    /**","     * Output debugging info via a user-selected method.","     *","     * @param string $str   Debug string to output","     * @param int    $level The debug level of this message; see DEBUG_* constants","     *","     * @see SMTP::$Debugoutput","     * @see SMTP::$do_debug","     */","    protected function edebug($str, $level = 0)","    {","        if ($level \u003e $this-\u003edo_debug) {","            return;","        }","        //Is this a PSR-3 logger?","        if ($this-\u003eDebugoutput instanceof \\Psr\\Log\\LoggerInterface) {","            //Remove trailing line breaks potentially added by calls to SMTP::client_send()","            $this-\u003eDebugoutput-\u003edebug(rtrim($str, \"\\r\\n\"));","","            return;","        }","        //Avoid clash with built-in function names","        if (is_callable($this-\u003eDebugoutput) \u0026\u0026 !in_array($this-\u003eDebugoutput, ['error_log', 'html', 'echo'])) {","            call_user_func($this-\u003eDebugoutput, $str, $level);","","            return;","        }","        switch ($this-\u003eDebugoutput) {","            case 'error_log':","                //Don't output, just log","                /** @noinspection ForgottenDebugOutputInspection */","                error_log($str);","                break;","            case 'html':","                //Cleans up output a bit for a better looking, HTML-safe output","                echo gmdate('Y-m-d H:i:s'), ' ', htmlentities(","                    preg_replace('/[\\r\\n]+/', '', $str),","                    ENT_QUOTES,","                    'UTF-8'","                ), \"\u003cbr\u003e\\n\";","                break;","            case 'echo':","            default:","                //Normalize line breaks","                $str = preg_replace('/\\r\\n|\\r/m', \"\\n\", $str);","                echo gmdate('Y-m-d H:i:s'),","                \"\\t\",","                    //Trim trailing space","                trim(","                    //Indent for readability, except for trailing break","                    str_replace(","                        \"\\n\",","                        \"\\n                   \\t                  \",","                        trim($str)","                    )","                ),","                \"\\n\";","        }","    }","","    /**","     * Connect to an SMTP server.","     *","     * @param string $host    SMTP server IP or host name","     * @param int    $port    The port number to connect to","     * @param int    $timeout How long to wait for the connection to open","     * @param array  $options An array of options for stream_context_create()","     *","     * @return bool","     */","    public function connect($host, $port = null, $timeout = 30, $options = [])","    {","        //Clear errors to avoid confusion","        $this-\u003esetError('');","        //Make sure we are __not__ connected","        if ($this-\u003econnected()) {","            //Already connected, generate error","            $this-\u003esetError('Already connected to a server');","","            return false;","        }","        if (empty($port)) {","            $port = self::DEFAULT_PORT;","        }","        //Connect to the SMTP server","        $this-\u003eedebug(","            \"Connection: opening to $host:$port, timeout=$timeout, options=\" .","            (count($options) \u003e 0 ? var_export($options, true) : 'array()'),","            self::DEBUG_CONNECTION","        );","","        $this-\u003esmtp_conn = $this-\u003egetSMTPConnection($host, $port, $timeout, $options);","","        if ($this-\u003esmtp_conn === false) {","            //Error info already set inside `getSMTPConnection()`","            return false;","        }","","        $this-\u003eedebug('Connection: opened', self::DEBUG_CONNECTION);","","        //Get any announcement","        $this-\u003elast_reply = $this-\u003eget_lines();","        $this-\u003eedebug('SERVER -\u003e CLIENT: ' . $this-\u003elast_reply, self::DEBUG_SERVER);","        $responseCode = (int)substr($this-\u003elast_reply, 0, 3);","        if ($responseCode === 220) {","            return true;","        }","        //Anything other than a 220 response means something went wrong","        //RFC 5321 says the server will wait for us to send a QUIT in response to a 554 error","        //https://www.rfc-editor.org/rfc/rfc5321#section-3.1","        if ($responseCode === 554) {","            $this-\u003equit();","        }","        //This will handle 421 responses which may not wait for a QUIT (e.g. if the server is being shut down)","        $this-\u003eedebug('Connection: closing due to error', self::DEBUG_CONNECTION);","        $this-\u003eclose();","        return false;","    }","","    /**","     * Create connection to the SMTP server.","     *","     * @param string $host    SMTP server IP or host name","     * @param int    $port    The port number to connect to","     * @param int    $timeout How long to wait for the connection to open","     * @param array  $options An array of options for stream_context_create()","     *","     * @return false|resource","     */","    protected function getSMTPConnection($host, $port = null, $timeout = 30, $options = [])","    {","        static $streamok;","        //This is enabled by default since 5.0.0 but some providers disable it","        //Check this once and cache the result","        if (null === $streamok) {","            $streamok = function_exists('stream_socket_client');","        }","","        $errno = 0;","        $errstr = '';","        if ($streamok) {","            $socket_context = stream_context_create($options);","            set_error_handler(function () {","                call_user_func_array([$this, 'errorHandler'], func_get_args());","            });","            $connection = stream_socket_client(","                $host . ':' . $port,","                $errno,","                $errstr,","                $timeout,","                STREAM_CLIENT_CONNECT,","                $socket_context","            );","        } else {","            //Fall back to fsockopen which should work in more places, but is missing some features","            $this-\u003eedebug(","                'Connection: stream_socket_client not available, falling back to fsockopen',","                self::DEBUG_CONNECTION","            );","            set_error_handler(function () {","                call_user_func_array([$this, 'errorHandler'], func_get_args());","            });","            $connection = fsockopen(","                $host,","                $port,","                $errno,","                $errstr,","                $timeout","            );","        }","        restore_error_handler();","","        //Verify we connected properly","        if (!is_resource($connection)) {","            $this-\u003esetError(","                'Failed to connect to server',","                '',","                (string) $errno,","                $errstr","            );","            $this-\u003eedebug(","                'SMTP ERROR: ' . $this-\u003eerror['error']","                . \": $errstr ($errno)\",","                self::DEBUG_CLIENT","            );","","            return false;","        }","","        //SMTP server can take longer to respond, give longer timeout for first read","        //Windows does not have support for this timeout function","        if (strpos(PHP_OS, 'WIN') !== 0) {","            $max = (int)ini_get('max_execution_time');","            //Don't bother if unlimited, or if set_time_limit is disabled","            if (0 !== $max \u0026\u0026 $timeout \u003e $max \u0026\u0026 strpos(ini_get('disable_functions'), 'set_time_limit') === false) {","                @set_time_limit($timeout);","            }","            stream_set_timeout($connection, $timeout, 0);","        }","","        return $connection;","    }","","    /**","     * Initiate a TLS (encrypted) session.","     *","     * @return bool","     */","    public function startTLS()","    {","        if (!$this-\u003esendCommand('STARTTLS', 'STARTTLS', 220)) {","            return false;","        }","","        //Allow the best TLS version(s) we can","        $crypto_method = STREAM_CRYPTO_METHOD_TLS_CLIENT;","","        //PHP 5.6.7 dropped inclusion of TLS 1.1 and 1.2 in STREAM_CRYPTO_METHOD_TLS_CLIENT","        //so add them back in manually if we can","        if (defined('STREAM_CRYPTO_METHOD_TLSv1_2_CLIENT')) {","            $crypto_method |= STREAM_CRYPTO_METHOD_TLSv1_2_CLIENT;","            $crypto_method |= STREAM_CRYPTO_METHOD_TLSv1_1_CLIENT;","        }","","        //Begin encrypted connection","            set_error_handler(function () {","                call_user_func_array([$this, 'errorHandler'], func_get_args());","            });","        $crypto_ok = stream_socket_enable_crypto(","            $this-\u003esmtp_conn,","            true,","            $crypto_method","        );","        restore_error_handler();","","        return (bool) $crypto_ok;","    }","","    /**","     * Perform SMTP authentication.","     * Must be run after hello().","     *","     * @see    hello()","     *","     * @param string $username The user name","     * @param string $password The password","     * @param string $authtype The auth type (CRAM-MD5, PLAIN, LOGIN, XOAUTH2)","     * @param OAuthTokenProvider $OAuth An optional OAuthTokenProvider instance for XOAUTH2 authentication","     *","     * @return bool True if successfully authenticated","     */","    public function authenticate(","        $username,","        $password,","        $authtype = null,","        $OAuth = null","    ) {","        if (!$this-\u003eserver_caps) {","            $this-\u003esetError('Authentication is not allowed before HELO/EHLO');","","            return false;","        }","","        if (array_key_exists('EHLO', $this-\u003eserver_caps)) {","            //SMTP extensions are available; try to find a proper authentication method","            if (!array_key_exists('AUTH', $this-\u003eserver_caps)) {","                $this-\u003esetError('Authentication is not allowed at this stage');","                //'at this stage' means that auth may be allowed after the stage changes","                //e.g. after STARTTLS","","                return false;","            }","","            $this-\u003eedebug('Auth method requested: ' . ($authtype ?: 'UNSPECIFIED'), self::DEBUG_LOWLEVEL);","            $this-\u003eedebug(","                'Auth methods available on the server: ' . implode(',', $this-\u003eserver_caps['AUTH']),","                self::DEBUG_LOWLEVEL","            );","","            //If we have requested a specific auth type, check the server supports it before trying others","            if (null !== $authtype \u0026\u0026 !in_array($authtype, $this-\u003eserver_caps['AUTH'], true)) {","                $this-\u003eedebug('Requested auth method not available: ' . $authtype, self::DEBUG_LOWLEVEL);","                $authtype = null;","            }","","            if (empty($authtype)) {","                //If no auth mechanism is specified, attempt to use these, in this order","                //Try CRAM-MD5 first as it's more secure than the others","                foreach (['CRAM-MD5', 'LOGIN', 'PLAIN', 'XOAUTH2'] as $method) {","                    if (in_array($method, $this-\u003eserver_caps['AUTH'], true)) {","                        $authtype = $method;","                        break;","                    }","                }","                if (empty($authtype)) {","                    $this-\u003esetError('No supported authentication methods found');","","                    return false;","                }","                $this-\u003eedebug('Auth method selected: ' . $authtype, self::DEBUG_LOWLEVEL);","            }","","            if (!in_array($authtype, $this-\u003eserver_caps['AUTH'], true)) {","                $this-\u003esetError(\"The requested authentication method \\\"$authtype\\\" is not supported by the server\");","","                return false;","            }","        } elseif (empty($authtype)) {","            $authtype = 'LOGIN';","        }","        switch ($authtype) {","            case 'PLAIN':","                //Start authentication","                if (!$this-\u003esendCommand('AUTH', 'AUTH PLAIN', 334)) {","                    return false;","                }","                //Send encoded username and password","                if (","                    //Format from https://www.rfc-editor.org/rfc/rfc4616#section-2","                    //We skip the first field (it's forgery), so the string starts with a null byte","                    !$this-\u003esendCommand(","                        'User \u0026 Password',","                        base64_encode(\"\\0\" . $username . \"\\0\" . $password),","                        235","                    )","                ) {","                    return false;","                }","                break;","            case 'LOGIN':","                //Start authentication","                if (!$this-\u003esendCommand('AUTH', 'AUTH LOGIN', 334)) {","                    return false;","                }","                if (!$this-\u003esendCommand('Username', base64_encode($username), 334)) {","                    return false;","                }","                if (!$this-\u003esendCommand('Password', base64_encode($password), 235)) {","                    return false;","                }","                break;","            case 'CRAM-MD5':","                //Start authentication","                if (!$this-\u003esendCommand('AUTH CRAM-MD5', 'AUTH CRAM-MD5', 334)) {","                    return false;","                }","                //Get the challenge","                $challenge = base64_decode(substr($this-\u003elast_reply, 4));","","                //Build the response","                $response = $username . ' ' . $this-\u003ehmac($challenge, $password);","","                //send encoded credentials","                return $this-\u003esendCommand('Username', base64_encode($response), 235);","            case 'XOAUTH2':","                //The OAuth instance must be set up prior to requesting auth.","                if (null === $OAuth) {","                    return false;","                }","                $oauth = $OAuth-\u003egetOauth64();","","                //Start authentication","                if (!$this-\u003esendCommand('AUTH', 'AUTH XOAUTH2 ' . $oauth, 235)) {","                    return false;","                }","                break;","            default:","                $this-\u003esetError(\"Authentication method \\\"$authtype\\\" is not supported\");","","                return false;","        }","","        return true;","    }","","    /**","     * Calculate an MD5 HMAC hash.","     * Works like hash_hmac('md5', $data, $key)","     * in case that function is not available.","     *","     * @param string $data The data to hash","     * @param string $key  The key to hash with","     *","     * @return string","     */","    protected function hmac($data, $key)","    {","        if (function_exists('hash_hmac')) {","            return hash_hmac('md5', $data, $key);","        }","","        //The following borrowed from","        //https://www.php.net/manual/en/function.mhash.php#27225","","        //RFC 2104 HMAC implementation for php.","        //Creates an md5 HMAC.","        //Eliminates the need to install mhash to compute a HMAC","        //by Lance Rushing","","        $bytelen = 64; //byte length for md5","        if (strlen($key) \u003e $bytelen) {","            $key = pack('H*', md5($key));","        }","        $key = str_pad($key, $bytelen, chr(0x00));","        $ipad = str_pad('', $bytelen, chr(0x36));","        $opad = str_pad('', $bytelen, chr(0x5c));","        $k_ipad = $key ^ $ipad;","        $k_opad = $key ^ $opad;","","        return md5($k_opad . pack('H*', md5($k_ipad . $data)));","    }","","    /**","     * Check connection state.","     *","     * @return bool True if connected","     */","    public function connected()","    {","        if (is_resource($this-\u003esmtp_conn)) {","            $sock_status = stream_get_meta_data($this-\u003esmtp_conn);","            if ($sock_status['eof']) {","                //The socket is valid but we are not connected","                $this-\u003eedebug(","                    'SMTP NOTICE: EOF caught while checking if connected',","                    self::DEBUG_CLIENT","           